import React,{useState,useEffect} from "react";
import {Route, Redirect} from "react-router-dom";
import axios from "axios";
import UserPost from "./UserPost";
import {Modal,Button,Form} from "react-bootstrap";



const ProtectedRoute = ({
    component: Component,
    ...rest
  }) => {
    function createPost(post){
      return <div>
          <UserPost   key={post._id}
                  title={post.title}
                  content = {post.content}
                  pubAt = {post.createdAt}
                  likes = {post.likes}
                  tags = {post.tags}
                  postBy = {post.publishedBy}

              />
      </div>
}

    //Modal configs
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [data,setData] = useState([]);
    useEffect(() => {
      const fetchData = async () => {
        const result = await axios(
          `http://localhost:5000/userpost/uid/${JSON.parse(localStorage.getItem('userData'))['uid']}`,
        );

        setData(result.data);
      };

      fetchData();
    }, []);
    return (
      <Route
        {...rest}





        render={props => {
          if (localStorage.getItem('userData')) {
            return <div className="container mt-5">
            <div className="row">
              <div className="col-lg-1">
              <img src={"https://avatars.dicebear.com/api/avataaars/"+JSON.parse(localStorage.getItem('userData'))['first_name']+JSON.parse(localStorage.getItem('userData'))['last_name']+".svg"} alt="avatar" className="rounded-circle img-fluid" width="75" height="75"/>
              </div>
              <div className="col-lg-6">
              <p className="text-muted display-4">{JSON.parse(localStorage.getItem('userData'))['first_name']+" "+JSON.parse(localStorage.getItem('userData'))['last_name']}</p>
              </div>
            </div>


            <p className="text-muted">{JSON.parse(localStorage.getItem('userData'))['uemail']}</p>
            <p className="text-muted">Last updated on :{Date(JSON.parse(localStorage.getItem('userData'))['lastupdated'])}</p>

            <button className="btn btn-danger"
        onClick={() => {
          localStorage.clear();
          props.history.push('/login');
          window.location.reload(false);
        }}
      >
        Logout
      </button>
        <button className="btn btn-primary ml-4" onClick={handleShow}>Edit Profile</button>
        <br/>
        <h1 className="display-6 mt-4">Your Posts</h1>
        <div className="row">
          <div className="col-lg-12">

          {data.length===0?"Please Wait....":data.map(createPost)}

          </div>
        </div>
      <Modal show={show} onHide={handleClose} animation={false} centered={true}>
      <Modal.Header closeButton>
        <Modal.Title>Edit Your Profile</Modal.Title>
      </Modal.Header>
      <Modal.Body>
      <Form>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>First Name</Form.Label>
    <Form.Control type="text" placeholder="First Name" name="first_name" value={JSON.parse(localStorage.getItem('userData'))['first_name']}/>
  </Form.Group>

  <Form.Group controlId="formBasicEmail">
    <Form.Label>Last Name</Form.Label>
    <Form.Control type="text" placeholder="Last Name" name="last_name" value={JSON.parse(localStorage.getItem('userData'))['last_name']}/>
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Email</Form.Label>
    <Form.Control type="email" placeholder="Email"  name="email" value={JSON.parse(localStorage.getItem('userData'))['uemail']}/>
  </Form.Group>
</Form>
      </Modal.Body>
      <Modal.Footer>
        <div>

        <Button variant="primary" onClick={()=>{
          console.log(e.target);
        }}>
          Save Changes
        </Button></div>
      </Modal.Footer>
        </Modal>
      </div>
      // <!---- Modal>
      ;
          } else {
            return (
              <Redirect
                to={{
                  pathname: "/login",
                  state: {
                    from: props.location
                  }
                }}
              />
            );
          }
        }}
      />

    );
  };


export default ProtectedRoute;
