import React from "react";
import {Route, Redirect} from "react-router-dom";



const ProtectedRoute = ({
    component: Component,
    ...rest
  }) => {
    return (
      <Route
        {...rest}
        render={props => {
          if (localStorage.getItem('userData')) {
            return <div className="container mt-5"><h1 className="display-3">My Profile</h1>
            <img src={"https://avatars.dicebear.com/api/initials/"+JSON.parse(localStorage.getItem('userData'))['name']+".svg"} alt=""/>
            <p className="text-muted">{JSON.parse(localStorage.getItem('userData'))['name']}</p>
            <p className="text-muted">{JSON.parse(localStorage.getItem('userData'))['uemail']}</p>
            <p className="text-muted">Last updated on :{Date(JSON.parse(localStorage.getItem('userData'))['lastupdated'])}</p>
            <button className="btn btn-danger"
        onClick={() => {
          localStorage.clear();
          props.history.push('/login');
          window.location.reload(false);
        }}
      >
        Logout
      </button>
        <button className="btn btn-primary ml-4">Edit Profile</button>
      </div>;
          } else {
            return (
              <Redirect
                to={{
                  pathname: "/login",
                  state: {
                    from: props.location
                  }
                }}
              />
            );
          }
        }}
      />

    );
  };


export default ProtectedRoute;
