import React from "react";
import 'font-awesome/css/font-awesome.min.css';
import axios from 'axios';


export default class Post extends React.Component{
    likPost(e){
        const id=e.target.id;
        axios.post(`http://localhost:5000/post/like/uid/${id}`,{id:JSON.parse(localStorage.getItem('userData'))['uid']}).then((response)=>{
            console.log(response);
                }).catch((error)=>{
                    console.log(error);
    });

    }

    unlikePost(e){

    }
    render(){
        return <div>
            <div className="card mb-4" style={{"maxWidth":"850px","margin":"auto"}}>
                    <div className="card-body">
                        <h1 className="card-title text-primary">{this.props.title}</h1>
                        <p className="text-muted">{this.props.content}</p>
                        <small>by :{this.props.postBy}</small><br/>
                        <span>Published at:</span><strong> {new Date(this.props.pubAt).toLocaleString()}</strong>
                        <p className="text-muted">Liked by :{this.props.likedBy}</p>
                    <i className="fa fa-like"/><p>  Likes: {this.props.likes}</p>
                    <strong>Tags:</strong>{this.props.tags.map(tag =>tag+",")}<br/>
                    {!this.props.likedBy.includes(JSON.parse(localStorage.getItem('userData'))['name'])?<button className="btn btn-link btn-lg ml-0" onClick={this.likPost} id={this.props.id}>Like</button>:<button className="btn btn-link btn-lg ml-0" id={this.props.id} onclick={this.unlikePost}>Unlike</button>}

                    </div>
                </div>
        </div>
    }
}
