import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Navbar,Nav} from 'react-bootstrap';

export default class NavbarCustom extends React.Component{

    state = {
        isLoggedIn: true
      }

      getUser(){
          return localStorage.getItem('username');
      }

      changeState(){
          if(localStorage.getItem('username')!=null){
              this.setState({isLoggedIn:true});
          }
      }


    render(){
       return  <Navbar bg="dark" expand="lg" variant="dark" className="p-3">
  <Navbar.Brand href="#home">DevBlog</Navbar.Brand>
  <Navbar.Toggle aria-controls="basic-navbar-nav" />
  <Navbar.Collapse id="basic-navbar-nav">
  <Nav className="mr-auto">
      <Nav.Link href="#home" onClick={this.changeState}>Home</Nav.Link>
      <Nav.Link href="#link">About</Nav.Link>
    </Nav>
    <Nav className="ml-auto">
        {this.state.isLoggedIn?<div><Nav.Link href="#link">{this.getUser()}</Nav.Link><Nav.Link href="#link">Logout</Nav.Link></div>:<Nav.Link href="#link">Login</Nav.Link>}

    </Nav>
  </Navbar.Collapse>
</Navbar>
    }
}
