import React from "react";
import 'font-awesome/css/font-awesome.min.css';


export default class Post extends React.Component{
    render(){
        return <div>
            <div className="card mb-4">
                    <div className="card-body">
                        <h1 className="card-title text-danger">{this.props.title}</h1>
                        <p className="text-muted">{this.props.content}</p>
                        <small>by :{this.props.postBy}</small><br/>
                        <span>Published at:</span><strong> {this.props.pubAt}</strong>
                    <i className="fa fa-like"/><p>  Likes: {this.props.likes}</p>
                    <strong>Tags:</strong>{this.props.tags.map(tag =>tag+",")}<br/>
                    {localStorage.getItem('userData')?<a href="#like" className="btn btn-primary btn-sm ml-0">Like</a>:null}

                    </div>
                </div>
        </div>
    }
}
