import React from "react";
import {Link} from "react-router-dom";

const CreatePost = (props)=>{
    if(localStorage.getItem('userData'))
    {

        function addPost(e){
            e.preventDefault();
            const form = e.target;
            const title = form.title.value;
            const decsription = form.decsription.value;
            if(title.length===0)
            {
                alert('Make sure to add title and post!')
            }
        }


    return(<div>
        <div className="container-fluid mt-5 ml-5">
	<div className="row">

	    <div className="col-md-8 col-md-offset-2">

    		<h1>Create post</h1>

    		<form method="POST" onSubmit={addPost}>

    		    <div className="form-group">
    		        <label>Title <span className="require">*</span></label>
    		        <input type="text" className="form-control" name="title" />
    		    </div>

    		    <div className="form-group">
    		        <label>Description <span className="require">*</span></label>
    		        <textarea rows="5" className="form-control" name="description" ></textarea>
    		    </div>

    		    <div className="form-group">
    		        <button type="submit" className="btn btn-dark">
    		            Create
    		        </button>
    		        <Link className="btn btn-default" to="/profile">
    		            Cancel
    		        </Link>
    		    </div>

    		</form>
		</div>

	</div>
</div>
    </div>);
}
else{
    return (

        window.location = "/login"

      );

}
}



export default CreatePost;
