import React from "react";
import 'font-awesome/css/font-awesome.min.css';


export default class Post extends React.Component{
    likPost(e){
        console.log(e.target);
    }
    render(){
        return <div>
            <div className="card mb-4" style={{"maxWidth":"850px","margin":"auto"}}>
                    <div className="card-body">
                        <h1 className="card-title text-primary">{this.props.title}</h1>
                        <p className="text-muted">{this.props.content}</p>
                        <small>by :{this.props.postBy}</small><br/>
                        <span>Published at:</span><strong> {new Date(this.props.pubAt).toLocaleString()}</strong>
                    <i className="fa fa-like"/><p>  Likes: {this.props.likes}</p>
                    <strong>Tags:</strong>{this.props.tags.map(tag =>tag+",")}<br/>
                    {localStorage.getItem('userData')?<button className="btn btn-link btn-lg ml-0" onClick={this.likPost} id={this.props.id}>Like</button>:null}

                    </div>
                </div>
        </div>
    }
}
