import React from "react";
import { Form, Button, Container, Row, Col } from "react-bootstrap";
const bcrypt = require('bcryptjs');
const axios = require('axios');


export default class SignUp extends React.Component {
  render() {
    return (
      <div>
        <Container>
          <Row style={{ margin: "150px 0" }}>
            <Col className="col-lg-6">
              <h1 className="display-3">Signup</h1>
              <Form>
                <Form.Group controlId="formFname">
                  <Form.Label>First Name</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="First Name"
                    autoComplete="false"
                    name="first_name"
                  />
                </Form.Group>

                <Form.Group controlId="formLname">
                  <Form.Label>Last Name</Form.Label>
                  <Form.Control 
                  type="text" 
                  placeholder="Last Name" 
                  name="last_name"
                  />
                </Form.Group>

                <Form.Group controlId="formBasicEmail">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control 
                  type="email" 
                  placeholder="Enter email"
                  name="email"
                   />
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control 
                  type="password"
                    placeholder="Password"
                    name="password1"
                     />
                </Form.Group>

                <Form.Group controlId="formBasicPassword2">
                  <Form.Label>Confirm Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Confirm Password"
                    name="password2"
                  />
                </Form.Group>
                <Button variant="dark" onClick={(e)=>{
                    const form = e.target.parentElement;
                    const first_name = form.first_name.value;
                    const last_name = form.last_name.value;
                    const email = form.email.value;
                    const password1 = form.password1.value;
                    const password2 = form.password2.value;
                    const reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
                    if(!first_name || !last_name || !email || !password1 || !password2)
                    {
                        console.log('All Fields Required');
                    }
                    else{
                        //Validate email using regex
                        if(reg.test(email)){
                            //check if password 1 is matched with password2
                            if(password1!=password2){
                                console.log('make sure two Password Matched!');
                            }
                            else{
                                console.log('All correct! now proceed');
                                // hash the plain text password to hashed password before saving to db
                                const saltRounds = 10;
                                bcrypt.genSalt(10, function(err, salt) {
                                    bcrypt.hash("B4c0/\/", salt, function(err, hash) {
                                        // Store hash in your password DB.
                                        const User = {
                                          first_name:first_name,
                                          last_name:last_name,
                                          email:email,
                                          password:hash,
                                        }
                                        axios.post('http://localhost:5000/register', User)
                                      .then((res) => {
                                        if(res.data){
                                          alert('Registration Complete!');

                                        }
                                          }).catch((error) => {
                                          console.log(error);
                                           });
                                    });
                                });

                            }
                        }
                        else{
                            console.log('Bad email');
                        }
                    }
                }}>
                  Register
                </Button>
              </Form>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
